customcss:
p{
    font-size: large;
}
.profile-pic{
    max-width: 200px;
}
.btn-01, .btn-02, .btn-03, .btn-04, .btn-05, .btn-06{
    cursor: pointer;
}
a:hover {
    text-decoration: none;
}
.astroid-header-center-balance-menu .astroid-nav>li>a .nav-title:after,
.astroid-stacked-divided-menu .astroid-nav>li>a .nav-title:after{
    background-color: #00bcc9;
}

*+.uk-margin-medium {
    margin-top: 0px !important;
}

.uk-card-body {
    padding: 0px 40px;
}

.astroid-header-center-balance {
    padding-top: 0px !important;
    padding-bottom: 15px !important;
}

.astroid-lightbox {
    position: relative;
}
.astroid-lightbox:after {
    content: ""; position: absolute; top: 50%; left: 50%;
    transform: translate(-50%,-50%); font-size: 30px; color: white;
    text-shadow: 0 1px 3px rgba(0,0,0,0.5);
}

.home-slide .sppb-container-inner .sppb-row{
    --bs-gutter-x: 0rem!important;
}

.sppb-video-block{
    border-radius: 15px;
}

.sermon h3 a {
    pointer-events: none;
}
.sermon-title{
    color: #00bcc9;
    line-height: 1.2rem;
    font-size: 0.95rem;
    margin-top: 10px;
    margin-bottom: 10px;
}
.sermon small, .sermon small a{
    color: #888888;
}
.sermon small a:hover {
    color: #009c77;
}
.sermon img{ 
    max-width: 500px;
    width: 100%;
    border-radius: 12px;
}

/* Force fulltext display in scroller */
.sppb-articles-scroller .article-intro,
.sppb-articles-scroller .article-fulltext {
    display: block !important;
    max-height: none !important;
}

/* Ensure JCE MediaBox works */
.sppb-articles-scroller .jcemediabox,
.sppb-articles-scroller a[data-mediabox] {
    display: block !important;
}

.error-page .container{
    padding: 20px;
    backdrop-filter: blur(15px);
    -webkit-backdrop-filter: blur(15px);
    border-radius: 20px;
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
}

.timetable table{
    width:100%;
    border-collapse: collapse;  /* Removes double borders */
}

.timetable th,td {
    border: 1px solid #ccc;
    color: #ffffff;
    /* Width + style + color shorthand */
    padding: 5px;
    text-align: left;
}
h3.module-title {
    font-size: 24px;
}
.footer-area{
    background-color: #003f1c;
    padding: 20px 0px;
}
.speasyimagegallery-gallery-item-title, .speasyimagegallery-image-content {
    display: none;
}

.sppb-addon-timeline .sppb-addon-timeline-wrapper .timeline-movement .timeline-item .timeline-panel{
    width: 555px;
}
.sppb-addon-timeline .sppb-addon-timeline-wrapper .timeline-badge{
    left: 49%;
}

em{
    color: unset;
}

ul.categories-module li a {
    font-size: unset;
}
ul.categories-module li h4 {
    font-size: 1rem;
    line-height: 0.5rem;
}
ul.categories-module li h5 {
    font-size: 0.8rem;
    text-indent: 20px;
}
.mod_breadcrumbs {
    user-select: none
}

.mod_breadcrumbs a {
    text-decoration: none;
}

.categories-module li:contains("(0)"){
    display: none;
}

.article-image-top img {
    border-radius: 12px;
    /* Adjust value for corner roundness */
    object-fit: cover;
    margin-bottom:15px;
}
.article-image-top .category-name, .article-image-top .category-name a {
        color: #f1c600;
}
.article-image-top .category-name a:hover {
    color: #00bcc9;
}
.article-image-top .uk-text-meta{
    line-height: 1;
    color: #00bcc9;
}

.speasyimagegallery-album > div > a:after, .speasyimagegallery-album img{
    border-radius: 12px !important;
}

/* home feature img */
.sppb-articles-carousel-img {
    border-radius: 12px 12px 0px 0px;
    height: 250px;
    overflow: hidden;
    display: flex;           /* Enables flex centering */
    /* align-items: center;    Vertical center */
    /* justify-content: center; Horizontal center */
}


li:has(.member-menu) {
    display: none !important;
}

.pastor1 .sppb-image-holder{
    border-radius: 15px 15px 0px 15px;
}

.pastor2 .sppb-image-holder {
    border-radius: 0px 15px 15px 15px;
}

.sppb-carousel-extended-list{
    border-radius: 15px;
}

.sppb-addon-article-layout img {
    bord0er-radius: 12px;
}



.home-news img {
border-radius: 12px 12px 0px 0px;
}

.home-news .sppb-articles-carousel-content{
border-radius: 0px 0px 12px 12px;

}
.home-news .sppb-articles-carousel-meta{
display: none;
}





/* ipad */
@media only screen and (max-width: 1024px) {
    
    .astroid-contact-info {
        display: none;
    }
    .sppb-articles-carousel-img {
         height: 150px;
    }
}


/* ipad mini */
@media only screen and (max-width: 820px) {
    .container {
            max-width: 820px;
    }
    .sppb-articles-carousel-img {
        height: 150px;
    }
    
    .astroid-contact-info {
        display: none;
    }
     .top-bar .container {
        padding: 0px 0px;
    }
    .top01{
        background-color: #003f1c;
        color: #ffffff;
    }
    .top01 a{
        color: #ffffff;
    }
    .top02 {
        -ms-flex-pack: left!important;
        justify-content: left !important;
    }
    
}


/* mobile */
@media only screen and (max-width: 480px) {

    .sppb-articles-carousel-img {
         height: unset;
    }

    .top02 {
        -ms-flex-pack: left!important;
        justify-content: left !important;
    }

    .top-links{
            margin: 0 auto !important;
    }

    .astroid-contact-info {
        display: none;
    }

    li:has(.member-menu) {
        display: block !important;
    }

    .footer-area a[href^="tel"]  {
            color: #ffffff;
    }
    .timetable {
        margin-bottom: 50px !important;
    }
    .sppb-addon-timeline .sppb-addon-timeline-wrapper .timeline-movement .timeline-item .timeline-panel {
        width: fit-content;
    }
    .sppb-addon-timeline .sppb-addon-timeline-wrapper .timeline-badge{
        left: -31px;
    }
    .top-bar .container {
        padding: 0px 0px;
    }
    .top01{
        background-color: #003f1c;
        color: #ffffff;
    }
    .top01 a{
        color: #ffffff;
    }
    .pastor .sppb-col-sm-6{
        margin-bottom: 20px;
    }
    .pastor2 .sppb-content-holder{
        padding: 20px 0px !important;
    }
    .pastor1 .sppb-image-holder {
        border-radius: 15px;
    }

    .pastor2 .sppb-image-holder {
        border-radius: 15px;
    }
    .sppb-carousel-extended-team-content {
       padding: 10px 10px 15px 10px;
       
    }
    .uk-breadcrumb>:nth-child(n+2):not(.uk-first-column)::before{
        margin: 0px 5px;
    }

    .sppb-addon-table-main td {
        padding: 2px;
    }
    .sppb-addon-table-main.bt tbody td::before,
    .sppb-addon-table-main.bt tfoot td::before,
    .sppb-addon-table-main.bt tfoot th::before{
        content: attr(data-th) "";
        text-align: right;
        padding: 0px 10px;
    }
    .sppb-addon-table-main tbody tr:nth-child(even) td:before {
        background: #c9cdec !important;
        color: #6c7ae0 !important;
    }
    

    .side-menu {
        margin-top: 30px !important;
    }
    .uk-margin-medium{
        margin-bottom: 20px;
    }


}

